import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import Card from "../Card/Card";
import * as actions from "../../redux/actions"
import styles from "./Home.module.css"


export default function Home() {

    const dispatch = useDispatch()
    const videogames = useSelector(state => state.videogames);

    useEffect(() => {
        if (!videogames.length)
            dispatch(actions.getAllVideogames())
    }, [])

    function handleOrder(event) {

    }

    function handleFilter(event) {

    }

    return (
        <div className={styles.container}>
            <select name="orderByLocation" onChange={handleOrder}>
                <option value="order" disabled="disabled" selected>Order By...</option>
                <option value="Ascendente">Ascendente</option>
                <option value="Descendente">Descendente</option>
            </select>
            <select name="orderByRating" onChange={handleOrder}>
                <option value="order" disabled="disabled" selected>Order By...</option>
                <option value="Ascendente">Ascendente</option>
                <option value="Descendente">Descendente</option>
            </select>
            <select name="gender" onChange={handleFilter}>
                <option value="filter" disabled="disabled" selected>Filter By...</option>
                <option value="Todos">Todos</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Genderless">Genderless</option>
                <option value="unknown">unknown</option>
            </select>
            {
                videogames.length ? (
                    videogames[0].map(game => {
                        return (
                            <Card
                                key={game.id}
                                id={game.id}
                                name={game.name}
                                image={game.background_image}
                                genres={game.genres.join(", ")}
                            />
                        )
                    })
                ) : <h1>LOADING...</h1>
            }
        </div>
    )
}
