import styles from "./Card.module.css";
import { Link } from "react-router-dom";

export default function Card(props) {

    return (
        <div className={styles.card}>
            <Link to={`/detail/${props.id}`} >
                <h3>{props.name}</h3>
            </Link>
            <img src={props.image} alt={props.name} />
            <h4>{props.genres}</h4>
        </div>
    )
}
