import React from "react";
import styles from "./Paginate.module.css"

export default function Paginate({ videogamesPerPage, videogames, paginate }) {
    
    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(videogames / videogamesPerPage); i++) {
        pageNumbers.push(i);
    }

    function handlePrev(event) {
        if(currentPage > 1) {
            setCurrentPage(currentPage - 1)
        }
    }
    function handleNext(event) {
        if(currentPage < 1) {
            setCurrentPage(currentPage - 1)
        }
    }

    return (
        <div className={styles.container}>
            <button className={styles.changePageButton} onClick={handlePrev}>prev</button>
            <ul>
                {pageNumbers &&
                    pageNumbers.map((number, index) => {
                        return (
                            <li key={index} className={styles.pagination}>
                                <a className={styles.page} href onClick={() => paginate(number)}>{number}</a>
                            </li>

                        )
                    })}
            </ul>
            <button className={styles.changePageButton} onClick={handleNext}>next</button>
        </div>
    )
}
