import { 
    GET_ALL_VIDEOGAMES,
    GET_GENRES,
    GET_VIDEOGAME_BY_NAME,
    GET_VIDEOGAME_BY_ID,
    CREATE_VIDEOGAME
    } from "./actionTypes"

const initialState = {
    myVideogames: [],
    allVideogames: [],
    genres: []
}

export default function reducer(state = initialState, { type, payload }) {
    switch(type) {
        case GET_ALL_VIDEOGAMES:
            return {
                allVideogames: [ ...state.allVideogames, payload]
            }
        case GET_GENRES:
            return {
                genres: [...state.genres, payload]
            }
        case GET_VIDEOGAME_BY_ID:
