import { useState } from "react";
//import { useNavigate } from "react-router-dom";
import styles from "./SearchBar.module.css"
import * as actions from "../../redux/actions";
import { useDispatch } from "react-redux";

export default function SearchBar() {
    const [videogame, setVideogame] = useState({
        name:""
    });
    //const navigate = useNavigate();
    const dispatch = useDispatch();


    function search() {     
        dispatch(actions.getVideogameByName(videogame.name))        
    }


    function save(event) {
        setVideogame({
            ...videogame,
            name: event.target.value 
        })
    };

    // function handleKeyDown(event) {
    //     if (event.key === 'Enter') {
    //         navigate("/home");
    //         search();
    //         videogame.id = "";
    //     }
    // };

    return (
        <div>
            <input value={videogame.name} onChange={save} className={styles.input} onKeyDown="" type='search' />
            <button className={styles.searchButton} onClick={search}>Search</button>
        </div>

    )
};
