import {
    GET_ALL_VIDEOGAMES,
    GET_GENRES,
} from "./actionTypes"

const initialState = {
    videogames: [],
    allVideogames: [],
    genres: []
}

export default function reducer(state = initialState, { type, payload }) {
    switch (type) {
        case GET_ALL_VIDEOGAMES:
            return {
                ...state,
                videogames: [...state.allVideogames, payload],
                allVideogames: [...state.allVideogames, payload]
            }
        case GET_GENRES:
            return {
                ...state,
                genres: [...state.genres, payload]
            }

        default:
            return { ...state }
    }
}
