import {
    GET_ALL_VIDEOGAMES,
    GET_GENRES,
    GET_VIDEOGAME_BY_NAME,
    FILTER_BY_GENRES,
    FILTER_BY_LOCATION,
    ORDER_BY_NAME,
    ORDER_BY_RATING
} from "./actionTypes"

const initialState = {
    videogames: [],
    allVideogames: [],
    genres: []
}

export default function reducer(state = initialState, { type, payload }) {
    switch (type) {
        case GET_ALL_VIDEOGAMES:
            return {
                ...state,
                videogames: payload,
                allVideogames: payload
            }
        case GET_GENRES:
            return {
                ...state,
                genres: payload
            }
        case GET_VIDEOGAME_BY_NAME:
            return {
                ...state,
                videogames: payload,
            }
        case ORDER_BY_NAME:
            if (payload === "Ascendente") {
                return {
                    ...state,
                    videogames: state.videogames.sort(((a, b) => a.name - b.name))
                }
            }
            if (payload === "Descendente") {
                return {
                    ...state,
                    videogames: state.videogames.sort(((a, b) => a.name - b.name)).reverse()
                }
            }
            return { ...state }
        case ORDER_BY_RATING:
            console.log(videogame);
            if (payload === "Ascendente") {
                let videogamesByRating = []
                videogamesByRating = state.allVideogames.slice().sort((x, y) => {
                    if (x.rating > y.rating) return 1
                    if (y.rating > x.rating) return -1
                    return 0
                })
                console.log(videogamesByRating);
                return {
                    ...state,
                    videogames: videogamesByRating
                }

            }
            if (payload === "Descendente") {
                return {
                    videogames: state.allVideogames.slice().sort((x, y) => {
                        if (x.rating > y.rating) return -1
                        if (y.rating > x.rating) return 1
                        return 0
                    })
                }
            }
            return { ...state }
        case FILTER_BY_GENRES:

            if (payload === "all") {
                return {
                    ...state,
                    videogames: state.allVideogames
                }
            }
            return {
                ...state,
                videogames: state.allVideogames.filter(videogame => {
                    if (videogame.genres.includes(payload)) return videogame
                    return null
                })
            }
        case FILTER_BY_LOCATION:
            if (payload === "all") {
                return {
                    ...state,
                    videogames: state.allVideogames
                }
            }
            if (payload === "database") {
                return {
                    ...state,
                    videogames: state.allVideogames.filter(videogame => videogame.createdInDb)
                }
            } else {
                return {
                    ...state,
                    videogames: state.allVideogames.filter(videogame => !videogame.createdInDb)
                }
            }
        default:
            return { ...state }
    }
}
