import { useState } from "react";
import styles from "./Form.module.css";


export default function Form() {
    const [inputs, setInputs] = useState({
        name: "",
        description: "",
        image: "",
        platforms: [],
        rating: "",
        released: "",
        genres: [],
    })


    function handleSubmit(event) {
        event.preventDefault()
    }

    function handleChange(event) {
        setInputs({
            ...inputs,
            [event.target.name]: event.target.value
        })
    }



    return (
        <div className={styles.container}>
            <form onSubmit={handleSubmit}>
                <label>Name:</label>
                <input name="name" value="" onChange={handleChange} placeholder="name..." type="" />
                <label>Description</label>
                <textarea name="description" value="" onChange={handleChange} placeholder="description..." type="" />
                <label>Platforms</label>
                <input name="platforms" value="" onChange={handleChange} placeholder="platforms..." type="" />
                <label>Image</label>
                <input name="image" value="" onChange={handleChange} placeholder="" type="URL image..." />
                <label>Released</label>
                <input name="released" value="" onChange={handleChange} placeholder="released..." type="" />
                <label>Rating</label>
                <input name="rating" value="" onChange={handleChange} placeholder="rating..." type="" />
                <button type="submit">Create</button>
            </form>

        </div>
    )
}







